import React, { useState } from "react";
import "./App.css"; // Import the CSS file

function App() {
  const [formData, setFormData] = useState({
    gender: "",
    age: "",
    hypertension: "",
    heart_disease: "",
    ever_married: "",
    work_type: "",
    Residence_type: "",
    avg_glucose_level: "",
    bmi: "",
    smoking_status: "",
  });

  const [prediction, setPrediction] = useState(null); // State for storing prediction result
  const [isOpen, setIsOpen] = useState(true); // Modal open state

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const response = await fetch("http://localhost:5000/predict", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(formData),
    });

    const result = await response.json();
    console.log("Response:", result);

    // Set the result in the state to display it on the page
    setPrediction(result.stroke);
  };

  const closeModal = () => {
    setIsOpen(false); // Close modal when the close button is clicked
  };

  return (
    <>
      {isOpen && (
        <div className="app-container">
          <div className="form-container">
            <button className="close-btn" onClick={closeModal}>
              Ã—
            </button>
            <h1 className="title">Stroke Prediction</h1>

            <form onSubmit={handleSubmit}>
              <div className="row">
                {/* Gender */}
                <div className="form-group">
                  <label>
                    Gender:
                    <select name="gender" onChange={handleChange} required>
                      <option value="">Select Gender</option>
                      <option value="Male">Male</option>
                      <option value="Female">Female</option>
                      <option value="Other">Other</option>
                    </select>
                  </label>
                </div>

                {/* Age */}
                <div className="form-group">
                  <label>
                    Age:
                    <input
                      type="number"
                      name="age"
                      onChange={handleChange}
                      placeholder="Age"
                      required
                    />
                  </label>
                </div>
              </div>

              {/* Additional form rows... */}

              <button type="submit">Predict Stroke</button>
            </form>
          </div>
        </div>
      )}

      {/* Show result below the form */}
      {prediction !== null && (
        <div className="result-container">
          <h3>Prediction Result</h3>
          <ul>
            <li>Predicted Stroke: {prediction ? "Yes" : "No"}</li>
          </ul>
        </div>
      )}
    </>
  );
}

export default App;
