import React, { useState } from 'react';
import { predictStroke } from './api';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function App() {
  const [formData, setFormData] = useState({});
  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const { data } = await predictStroke(formData);
      toast.success(`Prediction: ${data.prediction}`);
    } catch (error) {
      toast.error(`Error: ${error.response?.data?.error || 'Prediction failed'}`);
    }
  };

  return (
    <div>
      <h1>Stroke Prediction</h1>
      <form onSubmit={handleSubmit}>
        <input name="age" placeholder="Age" onChange={handleChange} required />
        <input name="bmi" placeholder="BMI" onChange={handleChange} required />
        <button type="submit">Predict</button>
      </form>
      <ToastContainer />
    </div>
  );
}
export default App;
